<?phpnamespace app\assets;use yii\web\AssetBundle;/** * @author Awsaf <awsaf.anam@gmail.com> * @since 1.0 */class AdminLTEAsset extends AssetBundle{    public $sourcePath = '@bower/admin-lte/';    public $css = [        'dist/css/AdminLTE.min.css',        'dist/css/skins/skin-green.min.css'    ];    public $js = [        'dist/js/app.js',        'plugins/slimScroll/jquery.slimscroll.min.js'    ];    public $depends = [        'yii\web\YiiAsset',        'yii\bootstrap\BootstrapAsset',        'yii\bootstrap\BootstrapPluginAsset',        'sersid\fontawesome\Asset'    ];        /**     * Initializes the bundle.     * Set publish options to copy only necessary files (in this case css and font folders)     */    public function init()    {        parent::init();        $this->publishOptions['beforeCopy'] = function ($from, $to) {            return preg_match('%[\\\/](dist|plugins)%', $from);        };    }}